INSERT INTO public.topics (id, examination_system_id, subject_id, level_id, term_id, title, description, summary_content, "order", created_at) VALUES ('topic-math-f3-commercial3', 'bde2015e-8e30-4460-ad2b-c79837d9438b', '547ffc3a-b94d-4010-b5fd-b65773c12328', 'c03f9705-e64b-438d-af6e-4e4b0f2e8093', 'c79e5e7a-1226-4ee8-97d4-c4601380e64c', 'Commercial Arithmetic III', 'Deals with foreign exchange, compound interest (further), and other commercial transactions.', '# Mathematics Notes for Secondary Level

## Table of Contents
