INSERT INTO public.questions (id, topic_id, question_text, option_a, option_b, option_c, option_d, correct_answer, explanation, difficulty, created_at) VALUES ('906a1f63-48da-41ae-88a0-20436f5bfdbd', NULL, 'What is the SI unit of force?', 'Newton', 'Joule', 'Watt', 'Pascal', 'A', 'The SI unit of force is the Newton, which is defined as the force required to accelerate a one-kilogram mass by one meter per second squared.', 'easy', '2025-09-09 06:29:32.036');
INSERT INTO public.questions (id, topic_id, question_text, option_a, option_b, option_c, option_d, correct_answer, explanation, difficulty, created_at) VALUES ('87445cb9-db0d-4bbf-89ce-0e6312872d00', NULL, 'Which of the following is a vector quantity?', 'Mass', 'Speed', 'Velocity', 'Temperature', 'C', 'Velocity is a vector quantity because it has both magnitude and direction, unlike speed which only has magnitude.', 'easy', '2025-09-09 06:29:32.087');
INSERT INTO public.questions (id, topic_id, question_text, option_a, option_b, option_c, option_d, correct_answer, explanation, difficulty, created_at) VALUES ('5f683329-802e-4766-8ad3-4dc0cf6ccf7f', NULL, 'What is the primary purpose of a fuse in an electrical circuit?', 'To increase the voltage', 'To reduce the resistance', 'To prevent excessive current flow', 'To increase the current', 'C', 'A fuse is used to protect an electrical circuit by breaking the circuit if the current exceeds a safe level, preventing overheating and potential fires.', 'easy', '2025-09-09 06:29:32.135');
INSERT INTO public.questions (id, topic_id, question_text, option_a, option_b, option_c, option_d, correct_answer, explanation, difficulty, created_at) VALUES ('2b1e9b40-ecde-4b6b-846c-34fb02cf0290', NULL, 'Which of the following instruments is used to measure atmospheric pressure?', 'Thermometer', 'Barometer', 'Hygrometer', 'Anemometer', 'B', 'A barometer is an instrument used to measure atmospheric pressure, which is essential for weather forecasting and altitude measurement.', 'easy', '2025-09-09 06:29:32.182');
INSERT INTO public.questions (id, topic_id, question_text, option_a, option_b, option_c, option_d, correct_answer, explanation, difficulty, created_at) VALUES ('43783404-3eb3-48d9-b9d3-64e44ba6c218', NULL, 'What type of image is formed by a plane mirror?', 'Real and inverted', 'Virtual and inverted', 'Real and upright', 'Virtual and upright', 'D', 'A plane mirror forms a virtual and upright image that is laterally inverted. Virtual images cannot be projected on a screen.', 'easy', '2025-09-09 06:29:32.229');
INSERT INTO public.questions (id, topic_id, question_text, option_a, option_b, option_c, option_d, correct_answer, explanation, difficulty, created_at) VALUES ('5ba2f696-a954-443e-bbb5-bc3ef359eb69', NULL, 'Which of the following is a non-renewable energy source?', 'Solar energy', 'Wind energy', 'Coal', 'Geothermal energy', 'C', 'Coal is a non-renewable energy source because it is formed from fossilized organic matter and takes millions of years to form, unlike renewable sources like solar and wind.', 'easy', '2025-09-09 06:29:32.277');
INSERT INTO public.questions (id, topic_id, question_text, option_a, option_b, option_c, option_d, correct_answer, explanation, difficulty, created_at) VALUES ('628fa8ad-031a-4d1b-a887-a400ab3583fc', NULL, 'Which principle explains why a ship floats on water?', 'Archimedes'' Principle', 'Pascal''s Principle', 'Bernoulli''s Principle', 'Newton''s First Law', 'A', 'Archimedes'' Principle states that an object submerged in fluid experiences a buoyant force equal to the weight of the fluid displaced, allowing a ship to float.', 'easy', '2025-09-09 06:29:32.325');
INSERT INTO public.questions (id, topic_id, question_text, option_a, option_b, option_c, option_d, correct_answer, explanation, difficulty, created_at) VALUES ('3185bc0b-fef0-499d-ac70-6bdd33f5a4f2', NULL, 'What is the main reason for using copper in electrical cables?', 'High density', 'High resistance', 'High conductivity', 'High cost', 'C', 'Copper is used in electrical cables primarily because of its high electrical conductivity, allowing efficient transmission of electricity.', 'easy', '2025-09-09 06:29:32.372');
INSERT INTO public.questions (id, topic_id, question_text, option_a, option_b, option_c, option_d, correct_answer, explanation, difficulty, created_at) VALUES ('042996e4-12eb-4bdd-ba7a-065cc1cd6701', NULL, 'What does an ammeter measure?', 'Voltage', 'Current', 'Resistance', 'Temperature', 'B', 'An ammeter is an instrument used to measure the electric current in a circuit, typically in amperes.', 'easy', '2025-09-09 06:29:32.422');
INSERT INTO public.questions (id, topic_id, question_text, option_a, option_b, option_c, option_d, correct_answer, explanation, difficulty, created_at) VALUES ('3821bdcc-593c-466d-8aae-111949c5dfc8', NULL, 'Which of the following is not a property of light?', 'Refraction', 'Diffusion', 'Diffraction', 'Reflection', 'B', 'Diffusion is not a property of light. It is the process of spreading something more widely, typically in gases or fluids. Light exhibits refraction, diffraction, and reflection.', 'easy', '2025-09-09 06:29:32.477');
INSERT INTO public.questions (id, topic_id, question_text, option_a, option_b, option_c, option_d, correct_answer, explanation, difficulty, created_at) VALUES ('d3a9c7cf-b026-4f41-b6ef-b7888075b5d0', NULL, 'What is the effect of increasing the length of a pendulum on its period?', 'Period decreases', 'Period increases', 'Period remains the same', 'Period doubles', 'B', 'The period of a pendulum is directly proportional to the square root of its length. Increasing the length increases the period.', 'medium', '2025-09-09 06:29:32.525');
INSERT INTO public.questions (id, topic_id, question_text, option_a, option_b, option_c, option_d, correct_answer, explanation, difficulty, created_at) VALUES ('3c029c00-e59e-4835-9947-1b35cc0ce460', NULL, 'Which law explains the relationship between current, voltage, and resistance?', 'Ohm''s Law', 'Kirchhoff''s Law', 'Coulomb''s Law', 'Faraday''s Law', 'A', 'Ohm''s Law states that the current through a conductor between two points is directly proportional to the voltage across the two points and inversely proportional to the resistance.', 'medium', '2025-09-09 06:29:32.571');
INSERT INTO public.questions (id, topic_id, question_text, option_a, option_b, option_c, option_d, correct_answer, explanation, difficulty, created_at) VALUES ('6995041b-f8b7-4a48-8a5d-4a82c1db188c', NULL, 'What is the acceleration due to gravity on the surface of the Earth?', '9.8 m/sÂ²', '6.67 m/sÂ²', '10.8 m/sÂ²', '5.5 m/sÂ²', 'A', 'The standard acceleration due to gravity on Earth''s surface is approximately 9.8 meters per second squared.', 'medium', '2025-09-09 06:29:32.619');
INSERT INTO public.questions (id, topic_id, question_text, option_a, option_b, option_c, option_d, correct_answer, explanation, difficulty, created_at) VALUES ('604f00c4-7609-4939-91ba-4163a023c327', NULL, 'What happens to the pressure of a gas if its volume is reduced while the temperature remains constant?', 'Pressure decreases', 'Pressure increases', 'Pressure remains the same', 'Pressure doubles', 'B', 'According to Boyle''s Law, the pressure of a gas is inversely proportional to its volume when temperature is constant. Therefore, reducing the volume increases the pressure.', 'medium', '2025-09-09 06:29:32.666');
INSERT INTO public.questions (id, topic_id, question_text, option_a, option_b, option_c, option_d, correct_answer, explanation, difficulty, created_at) VALUES ('cd64933c-e41b-4909-a952-e369d9d3d6f0', NULL, 'Which of the following is not a longitudinal wave?', 'Sound wave', 'Seismic P-wave', 'Water wave', 'Ultrasound wave', 'C', 'Water waves are not purely longitudinal; they are a combination of longitudinal and transverse motions. Longitudinal waves include sound and seismic P-waves.', 'medium', '2025-09-09 06:29:32.714');
INSERT INTO public.questions (id, topic_id, question_text, option_a, option_b, option_c, option_d, correct_answer, explanation, difficulty, created_at) VALUES ('c999f4ad-be4f-473f-91bc-8d637e238a90', NULL, 'What is the primary function of a transformer?', 'To increase resistance', 'To convert DC to AC', 'To change voltage levels', 'To store electrical energy', 'C', 'A transformer is used to change the voltage levels of alternating current (AC) in power systems, either stepping up or stepping down the voltage.', 'medium', '2025-09-09 06:29:32.76');
INSERT INTO public.questions (id, topic_id, question_text, option_a, option_b, option_c, option_d, correct_answer, explanation, difficulty, created_at) VALUES ('6ca1daa8-5724-4392-8a31-8e875d72c2b9', NULL, 'Which of the following best describes the term ''temperature''?', 'The amount of heat energy in a substance', 'The average kinetic energy of particles in a substance', 'The total energy of all particles in a substance', 'The potential energy of particles in a substance', 'B', 'Temperature is a measure of the average kinetic energy of the particles in a substance. It indicates how hot or cold the substance is.', 'medium', '2025-09-09 06:29:32.808');
INSERT INTO public.questions (id, topic_id, question_text, option_a, option_b, option_c, option_d, correct_answer, explanation, difficulty, created_at) VALUES ('71d2d447-a038-44e1-b80f-872e0a534005', NULL, 'Which of the following materials is the best conductor of electricity?', 'Rubber', 'Glass', 'Copper', 'Plastic', 'C', 'Copper is a metal with high electrical conductivity, making it an excellent conductor of electricity, unlike insulators like rubber, glass, and plastic.', 'medium', '2025-09-09 06:29:32.856');
INSERT INTO public.questions (id, topic_id, question_text, option_a, option_b, option_c, option_d, correct_answer, explanation, difficulty, created_at) VALUES ('39b0aaae-238b-42c0-8b2f-832f9645548e', NULL, 'What determines the pitch of a sound?', 'Amplitude', 'Frequency', 'Speed', 'Wavelength', 'B', 'The pitch of a sound is determined by its frequency. Higher frequency sounds have a higher pitch, while lower frequency sounds have a lower pitch.', 'medium', '2025-09-09 06:29:32.904');
INSERT INTO public.questions (id, topic_id, question_text, option_a, option_b, option_c, option_d, correct_answer, explanation, difficulty, created_at) VALUES ('e85d3dae-5c0f-4268-9075-b59848e1e98a', NULL, 'Which of the following best describes inertia?', 'The ability of an object to resist changes in motion', 'The force exerted by a moving object', 'The speed of an object in free fall', 'The energy stored in an object', 'A', 'Inertia is the property of an object to resist changes in its state of motion. It is directly related to the mass of the object.', 'medium', '2025-09-09 06:29:32.951');
INSERT INTO public.questions (id, topic_id, question_text, option_a, option_b, option_c, option_d, correct_answer, explanation, difficulty, created_at) VALUES ('58496c6b-cee7-406c-b467-7b8df73603ff', NULL, 'Which process describes the transfer of heat through a solid?', 'Convection', 'Radiation', 'Conduction', 'Evaporation', 'C', 'Conduction is the transfer of heat through a solid material where heat is passed from particle to particle through direct contact.', 'medium', '2025-09-09 06:29:32.999');
INSERT INTO public.questions (id, topic_id, question_text, option_a, option_b, option_c, option_d, correct_answer, explanation, difficulty, created_at) VALUES ('e2adcdd5-97b0-446c-9206-b6d10d96fe50', NULL, 'What occurs to light as it passes through a prism?', 'It absorbs', 'It refracts', 'It reflects', 'It diffuses', 'B', 'Light refracts, or bends, as it passes through a prism, resulting in the dispersion of white light into its constituent colors, forming a spectrum.', 'medium', '2025-09-09 06:29:33.047');
INSERT INTO public.questions (id, topic_id, question_text, option_a, option_b, option_c, option_d, correct_answer, explanation, difficulty, created_at) VALUES ('ae847339-46f1-46fa-8064-53d796c466f2', NULL, 'Which of the following is not an example of a mechanical wave?', 'Sound wave', 'Seismic wave', 'Light wave', 'Water wave', 'C', 'Light waves are electromagnetic waves, not mechanical. Mechanical waves require a medium to travel through, such as sound, seismic, and water waves.', 'medium', '2025-09-09 06:29:33.095');
INSERT INTO public.questions (id, topic_id, question_text, option_a, option_b, option_c, option_d, correct_answer, explanation, difficulty, created_at) VALUES ('ecf916d1-dcee-4d2d-bbf4-9daa4af61bf6', NULL, 'What is the primary cause of tides on Earth?', 'The Earth''s rotation', 'The gravitational pull of the Moon', 'The gravitational pull of the Sun', 'The wind patterns', 'B', 'Tides are primarily caused by the gravitational pull of the Moon on the Earth''s oceans, with the Sun contributing to a lesser extent.', 'medium', '2025-09-09 06:29:33.146');
INSERT INTO public.questions (id, topic_id, question_text, option_a, option_b, option_c, option_d, correct_answer, explanation, difficulty, created_at) VALUES ('f3d0261a-aa3d-4f82-a648-0c1ad0906740', NULL, 'What is the term for the change in frequency of a wave in relation to an observer moving relative to the source?', 'Doppler Effect', 'Refraction', 'Diffraction', 'Interference', 'A', 'The Doppler Effect refers to the change in frequency or wavelength of a wave in relation to an observer who is moving relative to the wave source.', 'medium', '2025-09-09 06:29:33.193');
INSERT INTO public.questions (id, topic_id, question_text, option_a, option_b, option_c, option_d, correct_answer, explanation, difficulty, created_at) VALUES ('a6841427-d847-4c45-8ed0-4725eee02db4', NULL, 'How does the speed of sound in air compare to its speed in water?', 'Faster in air', 'Faster in water', 'Same speed', 'Depends on temperature', 'B', 'The speed of sound is faster in water than in air because water is denser, allowing sound waves to travel more efficiently between the molecules.', 'medium', '2025-09-09 06:29:33.24');
INSERT INTO public.questions (id, topic_id, question_text, option_a, option_b, option_c, option_d, correct_answer, explanation, difficulty, created_at) VALUES ('10c17084-8bf7-4163-b21a-219f583cc692', NULL, 'Which of the following phenomena demonstrates that light can behave as a wave?', 'Photoelectric effect', 'Reflection', 'Interference', 'Refraction', 'C', 'Interference is a wave phenomenon where two or more light waves superpose to form a resultant wave, demonstrating the wave nature of light.', 'medium', '2025-09-09 06:29:33.288');
INSERT INTO public.questions (id, topic_id, question_text, option_a, option_b, option_c, option_d, correct_answer, explanation, difficulty, created_at) VALUES ('785a89bd-a975-4a5e-b31d-c49a5290ab61', NULL, 'What type of mirror can be used to focus light to a single point?', 'Plane mirror', 'Convex mirror', 'Concave mirror', 'Spherical mirror', 'C', 'A concave mirror can focus parallel rays of light to a single point known as the focal point. This property is used in devices like telescopes.', 'medium', '2025-09-09 06:29:33.335');
INSERT INTO public.questions (id, topic_id, question_text, option_a, option_b, option_c, option_d, correct_answer, explanation, difficulty, created_at) VALUES ('15bcda72-e05f-470e-9831-cce9887e9afd', NULL, 'Which of the following best describes the term ''work'' in physics?', 'The energy transferred by a force over a distance', 'The power expended over time', 'The pressure applied over an area', 'The heat transferred between objects', 'A', 'In physics, work is defined as the energy transferred to or from an object via the application of force along a displacement.', 'medium', '2025-09-09 06:29:33.383');
INSERT INTO public.questions (id, topic_id, question_text, option_a, option_b, option_c, option_d, correct_answer, explanation, difficulty, created_at) VALUES ('cd287984-8a67-41de-8f15-15b2b6ba9fac', NULL, 'What is the term for the bending of waves around obstacles and openings?', 'Reflection', 'Refraction', 'Diffraction', 'Interference', 'C', 'Diffraction is the bending of waves around the corners of an obstacle or through an aperture, which is a property of wave behavior.', 'medium', '2025-09-09 06:29:33.432');
INSERT INTO public.questions (id, topic_id, question_text, option_a, option_b, option_c, option_d, correct_answer, explanation, difficulty, created_at) VALUES ('766aed51-9e95-4c0f-a663-8d88ea8f81da', NULL, 'Which of the following is a unit of force?', 'Newton', 'Pascal', 'Joule', 'Watt', 'A', 'The Newton is the unit of force in the International System of Units (SI). One Newton is the force required to accelerate one kilogram of mass at the rate of one meter per second squared.', 'easy', '2025-09-09 06:29:33.479');
INSERT INTO public.questions (id, topic_id, question_text, option_a, option_b, option_c, option_d, correct_answer, explanation, difficulty, created_at) VALUES ('02b6b3c7-be6f-4583-b13e-7bf5ac02d1e3', NULL, 'What is the primary source of energy for the Earth?', 'The Sun', 'Wind', 'Geothermal energy', 'Nuclear energy', 'A', 'The Sun is the primary source of energy for Earth. It provides the energy necessary for climate and weather systems, and supports life through photosynthesis in plants.', 'easy', '2025-09-09 06:29:33.544');
INSERT INTO public.questions (id, topic_id, question_text, option_a, option_b, option_c, option_d, correct_answer, explanation, difficulty, created_at) VALUES ('910ee492-a876-4c67-9e3c-f9a756e4e7e6', NULL, 'Which of the following instruments is used to measure mass?', 'Spring balance', 'Thermometer', 'Stopwatch', 'Beam balance', 'D', 'A beam balance is used to measure mass by comparing the mass of an object with known masses. It provides a direct measure of an object''s mass.', 'easy', '2025-09-09 06:29:33.596');
INSERT INTO public.questions (id, topic_id, question_text, option_a, option_b, option_c, option_d, correct_answer, explanation, difficulty, created_at) VALUES ('d2d050a3-86aa-442f-a48a-04defd5405ea', NULL, 'In which state of matter do particles have the most energy?', 'Solid', 'Liquid', 'Gas', 'Plasma', 'D', 'Plasma, often referred to as the fourth state of matter, consists of highly energized particles that have the ability to conduct electricity and respond to magnetic fields. It has more energy than solids, liquids, or gases.', 'medium', '2025-09-09 06:29:33.644');
INSERT INTO public.questions (id, topic_id, question_text, option_a, option_b, option_c, option_d, correct_answer, explanation, difficulty, created_at) VALUES ('0857c498-c9e3-4e85-b8e0-bbb691cb91be', NULL, 'What is the term for the transfer of heat through direct contact?', 'Radiation', 'Convection', 'Conduction', 'Evaporation', 'C', 'Conduction is the process of heat transfer through direct contact of molecules within a substance. It occurs when heat is transferred from a hotter part to a cooler part through molecules colliding.', 'easy', '2025-09-09 06:29:33.691');
INSERT INTO public.questions (id, topic_id, question_text, option_a, option_b, option_c, option_d, correct_answer, explanation, difficulty, created_at) VALUES ('702a1f02-1a08-42a3-9e46-ab6c936ba9b3', NULL, 'Which of the following physical quantities is a vector?', 'Mass', 'Temperature', 'Velocity', 'Energy', 'C', 'Velocity is a vector quantity because it has both magnitude and direction. In contrast, mass, temperature, and energy are scalar quantities with magnitude only.', 'medium', '2025-09-09 06:29:33.739');
INSERT INTO public.questions (id, topic_id, question_text, option_a, option_b, option_c, option_d, correct_answer, explanation, difficulty, created_at) VALUES ('d6f7c274-68e7-423c-b45a-5e908e742faa', NULL, 'Which of the following is the best conductor of electricity?', 'Copper', 'Wood', 'Glass', 'Rubber', 'A', 'Copper is a metal and is well known for its excellent electrical conductivity, which makes it widely used in electrical wiring.', 'medium', '2025-09-09 06:29:33.786');
INSERT INTO public.questions (id, topic_id, question_text, option_a, option_b, option_c, option_d, correct_answer, explanation, difficulty, created_at) VALUES ('b2aafe79-2690-4f38-985e-da73bc18432b', NULL, 'What is the acceleration due to gravity on Earth''s surface?', '9.8 m/sÂ²', '8.9 m/sÂ²', '10 m/sÂ²', '9.2 m/sÂ²', 'A', 'The standard acceleration due to gravity on Earth''s surface is approximately 9.8 m/sÂ². This value is used in equations of motion and other calculations involving gravity.', 'easy', '2025-09-09 06:29:33.834');
INSERT INTO public.questions (id, topic_id, question_text, option_a, option_b, option_c, option_d, correct_answer, explanation, difficulty, created_at) VALUES ('feb0fdff-959a-4566-9cd0-72f3b422c853', NULL, 'Which of the following is not a renewable source of energy?', 'Solar energy', 'Wind energy', 'Coal', 'Hydroelectric power', 'C', 'Coal is a fossil fuel and is not renewable because it takes millions of years to form. Solar, wind, and hydroelectric power are renewable as they are naturally replenished.', 'medium', '2025-09-09 06:29:33.882');
INSERT INTO public.questions (id, topic_id, question_text, option_a, option_b, option_c, option_d, correct_answer, explanation, difficulty, created_at) VALUES ('232a3dc5-4bdb-4a00-bea3-a4a27883debe', NULL, 'Which property of a wave is measured in Hertz?', 'Frequency', 'Amplitude', 'Wavelength', 'Speed', 'A', 'Frequency is the number of cycles of a wave passing a point per unit time, measured in Hertz (Hz). One Hertz is equivalent to one cycle per second.', 'medium', '2025-09-09 06:29:33.929');
INSERT INTO public.questions (id, topic_id, question_text, option_a, option_b, option_c, option_d, correct_answer, explanation, difficulty, created_at) VALUES ('5b8c0bad-d42d-48ff-a1e1-feb62b2dd722', NULL, 'What is the term for materials that do not allow heat to pass through them easily?', 'Conductors', 'Insulators', 'Metalloids', 'Semiconductors', 'B', 'Insulators are materials that do not allow heat to pass through them easily. They are used to prevent heat loss or gain, such as in thermal insulation.', 'easy', '2025-09-09 06:29:33.978');
INSERT INTO public.questions (id, topic_id, question_text, option_a, option_b, option_c, option_d, correct_answer, explanation, difficulty, created_at) VALUES ('ff097d62-7db2-4f05-a6ac-a6a155c565e4', NULL, 'Which law explains why a boat moves forward when a person pushes water backward with a paddle?', 'Newton''s First Law', 'Newton''s Second Law', 'Newton''s Third Law', 'Law of Conservation of Energy', 'C', 'Newton''s Third Law states that for every action, there is an equal and opposite reaction. When the person pushes the water backward, the water pushes the boat forward with an equal force.', 'medium', '2025-09-09 06:29:34.025');
INSERT INTO public.questions (id, topic_id, question_text, option_a, option_b, option_c, option_d, correct_answer, explanation, difficulty, created_at) VALUES ('fe926186-f7bf-40fc-973e-41393f692729', NULL, 'Which of the following is the correct formula for calculating density?', 'Density = Mass Ã— Volume', 'Density = Volume / Mass', 'Density = Mass / Volume', 'Density = Mass + Volume', 'C', 'Density is defined as mass per unit volume, and the formula is Density = Mass / Volume. It measures how much mass is contained in a given volume.', 'easy', '2025-09-09 06:29:34.071');
INSERT INTO public.questions (id, topic_id, question_text, option_a, option_b, option_c, option_d, correct_answer, explanation, difficulty, created_at) VALUES ('d56cc978-12ad-44a8-bcbf-57f7027f85dd', NULL, 'What is the term for the bending of light as it passes from one medium to another?', 'Reflection', 'Refraction', 'Diffraction', 'Interference', 'B', 'Refraction is the bending of light as it passes from one medium to another with a different density. This change in speed causes the light to change direction.', 'medium', '2025-09-09 06:29:34.119');
INSERT INTO public.questions (id, topic_id, question_text, option_a, option_b, option_c, option_d, correct_answer, explanation, difficulty, created_at) VALUES ('18e83416-148b-4334-a0ee-d1e445e5adae', NULL, 'Which of the following is an example of a non-contact force?', 'Friction', 'Tension', 'Magnetic force', 'Normal force', 'C', 'Magnetic force is a non-contact force because it acts at a distance without direct physical contact. It can attract or repel magnetic materials.', 'medium', '2025-09-09 06:29:34.167');
INSERT INTO public.questions (id, topic_id, question_text, option_a, option_b, option_c, option_d, correct_answer, explanation, difficulty, created_at) VALUES ('1ac04e8d-86ab-4b02-8c67-a979c8e2c821', NULL, 'Which of the following states that energy cannot be created or destroyed, only transformed?', 'Conservation of Mass', 'Conservation of Energy', 'Conservation of Momentum', 'Conservation of Charge', 'B', 'The Law of Conservation of Energy states that energy cannot be created or destroyed, only transformed from one form to another. The total energy in a closed system remains constant.', 'medium', '2025-09-09 06:29:34.215');
INSERT INTO public.questions (id, topic_id, question_text, option_a, option_b, option_c, option_d, correct_answer, explanation, difficulty, created_at) VALUES ('a2a32308-b8eb-4af7-ac53-b128fbced57e', NULL, 'Which of these devices converts electrical energy into mechanical energy?', 'Electric generator', 'Electric motor', 'Transformer', 'Battery', 'B', 'An electric motor converts electrical energy into mechanical energy, allowing it to do work, such as turning the blades of a fan.', 'medium', '2025-09-09 06:29:34.262');
INSERT INTO public.questions (id, topic_id, question_text, option_a, option_b, option_c, option_d, correct_answer, explanation, difficulty, created_at) VALUES ('0bc3876f-6bf2-4d8d-bf34-f9cf16820222', NULL, 'Which of the following is the correct unit for measuring power?', 'Joule', 'Newton', 'Watt', 'Pascal', 'C', 'Power is measured in Watts, which is equivalent to one Joule per second. It quantifies the rate at which work is done or energy is transferred.', 'easy', '2025-09-09 06:29:34.313');
INSERT INTO public.questions (id, topic_id, question_text, option_a, option_b, option_c, option_d, correct_answer, explanation, difficulty, created_at) VALUES ('45b8ed1a-6257-4e58-abbe-a9f2ba7bd125', NULL, 'Which phenomenon explains the increase in volume of a gas when its temperature is increased at constant pressure?', 'Boyle''s Law', 'Charles''s Law', 'Pascal''s Principle', 'Bernoulli''s Principle', 'B', 'Charles''s Law states that the volume of a gas is directly proportional to its temperature at constant pressure. As temperature increases, so does volume.', 'medium', '2025-09-09 06:29:34.359');
INSERT INTO public.questions (id, topic_id, question_text, option_a, option_b, option_c, option_d, correct_answer, explanation, difficulty, created_at) VALUES ('77829d6a-d2ea-42ec-85a6-83da1a779396', NULL, 'What is the term for the force that opposes the relative motion of two surfaces in contact?', 'Tension', 'Normal force', 'Friction', 'Centripetal force', 'C', 'Friction is the force that opposes the relative motion or tendency of such motion of two surfaces in contact. It acts parallel to the surfaces.', 'easy', '2025-09-09 06:29:34.407');
INSERT INTO public.questions (id, topic_id, question_text, option_a, option_b, option_c, option_d, correct_answer, explanation, difficulty, created_at) VALUES ('2b6d63ba-7fb5-4442-b276-52aa19855360', NULL, 'Which of the following elements is used in thermometers because it expands uniformly?', 'Water', 'Mercury', 'Alcohol', 'Oil', 'B', 'Mercury is used in thermometers because it expands uniformly with temperature changes, allowing for accurate measurements.', 'medium', '2025-09-09 06:29:34.454');
INSERT INTO public.questions (id, topic_id, question_text, option_a, option_b, option_c, option_d, correct_answer, explanation, difficulty, created_at) VALUES ('a8187ccc-ef7f-4d4e-b073-f79fc62c0307', NULL, 'What is the term for a material''s resistance to flow?', 'Density', 'Viscosity', 'Elasticity', 'Conductivity', 'B', 'Viscosity is a measure of a fluid''s resistance to flow. Higher viscosity means the fluid flows more slowly, like honey compared to water.', 'medium', '2025-09-09 06:29:34.501');
INSERT INTO public.questions (id, topic_id, question_text, option_a, option_b, option_c, option_d, correct_answer, explanation, difficulty, created_at) VALUES ('3696c833-ed4b-4769-9f5d-2fee43e7e6c3', NULL, 'Which of these particles is negatively charged?', 'Proton', 'Electron', 'Neutron', 'Photon', 'B', 'Electrons are negatively charged particles that orbit the nucleus of an atom. Protons are positively charged, while neutrons have no charge.', 'easy', '2025-09-09 06:29:34.55');
INSERT INTO public.questions (id, topic_id, question_text, option_a, option_b, option_c, option_d, correct_answer, explanation, difficulty, created_at) VALUES ('de685840-5c42-454c-9269-8e35178db100', NULL, 'What is the name of the point where the entire weight of an object appears to act?', 'Centre of gravity', 'Centre of mass', 'Equilibrium point', 'Centre of motion', 'A', 'The centre of gravity is the point where the entire weight of an object appears to act, and it is the balance point of the object.', 'medium', '2025-09-09 06:29:34.598');
INSERT INTO public.questions (id, topic_id, question_text, option_a, option_b, option_c, option_d, correct_answer, explanation, difficulty, created_at) VALUES ('1f9938df-2e86-498a-af59-94bfcefc0522', NULL, 'Which of the following is a scalar quantity?', 'Displacement', 'Force', 'Speed', 'Velocity', 'C', 'Speed is a scalar quantity because it only has magnitude and no direction, unlike velocity which is a vector quantity.', 'medium', '2025-09-09 06:29:34.645');
INSERT INTO public.questions (id, topic_id, question_text, option_a, option_b, option_c, option_d, correct_answer, explanation, difficulty, created_at) VALUES ('98cec6b0-ff89-44f8-9e4a-687822546139', NULL, 'What is the name of the process by which a liquid changes into a gas at its surface?', 'Boiling', 'Condensation', 'Evaporation', 'Sublimation', 'C', 'Evaporation is the process by which molecules at the surface of a liquid gain enough energy to become a gas. It occurs at temperatures below boiling point.', 'easy', '2025-09-09 06:29:34.694');
INSERT INTO public.questions (id, topic_id, question_text, option_a, option_b, option_c, option_d, correct_answer, explanation, difficulty, created_at) VALUES ('f21ca8a7-7ac2-4744-9f16-537f27dce602', NULL, 'Which of the following is not a form of electromagnetic radiation?', 'Sound waves', 'X-rays', 'Microwaves', 'Infrared', 'A', 'Sound waves are mechanical waves, not electromagnetic waves. Electromagnetic radiation includes X-rays, microwaves, and infrared.', 'medium', '2025-09-09 06:29:34.741');
INSERT INTO public.questions (id, topic_id, question_text, option_a, option_b, option_c, option_d, correct_answer, explanation, difficulty, created_at) VALUES ('0cb5e449-4d8c-43df-9fc1-4536c5ce4f53', NULL, 'Which force keeps the planets in orbit around the Sun?', 'Magnetic force', 'Electrostatic force', 'Gravitational force', 'Nuclear force', 'C', 'Gravitational force is the force of attraction between two masses. It is the force that keeps the planets in orbit around the Sun.', 'easy', '2025-09-09 06:29:34.789');
INSERT INTO public.questions (id, topic_id, question_text, option_a, option_b, option_c, option_d, correct_answer, explanation, difficulty, created_at) VALUES ('575a2033-8d12-47ae-9da8-c9ad6f83ff7f', NULL, 'What is the name of the device that measures atmospheric pressure?', 'Thermometer', 'Barometer', 'Ammeter', 'Voltmeter', 'B', 'A barometer is an instrument used to measure atmospheric pressure. It is commonly used in weather forecasting.', 'easy', '2025-09-09 06:29:34.837');
INSERT INTO public.questions (id, topic_id, question_text, option_a, option_b, option_c, option_d, correct_answer, explanation, difficulty, created_at) VALUES ('ea60932b-1cfd-42c2-b066-9f6f3e02f03d', NULL, 'What is the term for the force exerted by a fluid on an object moving through it?', 'Drag', 'Lift', 'Thrust', 'Buoyancy', 'A', 'Drag is the force exerted by a fluid (such as air or water) that opposes an object''s motion through the fluid. It is a form of friction.', 'medium', '2025-09-09 06:29:34.888');
INSERT INTO public.questions (id, topic_id, question_text, option_a, option_b, option_c, option_d, correct_answer, explanation, difficulty, created_at) VALUES ('54746047-aceb-4db3-9449-0861447025dc', NULL, 'Which of the following describes the tendency of an object to resist changes in its state of motion?', 'Inertia', 'Momentum', 'Force', 'Velocity', 'A', 'Inertia is the property of matter that causes it to resist changes in its state of motion. It depends on the mass of the object.', 'easy', '2025-09-09 06:29:34.937');
INSERT INTO public.questions (id, topic_id, question_text, option_a, option_b, option_c, option_d, correct_answer, explanation, difficulty, created_at) VALUES ('8a415d96-7b37-485f-80e4-4be0ed0cb198', NULL, 'Which of the following is an example of a scalar quantity?', 'Velocity', 'Force', 'Speed', 'Displacement', 'C', 'Speed is a scalar quantity because it only has magnitude and no direction, unlike velocity, force, and displacement which are vectors.', 'easy', '2025-09-09 06:29:34.985');
INSERT INTO public.questions (id, topic_id, question_text, option_a, option_b, option_c, option_d, correct_answer, explanation, difficulty, created_at) VALUES ('4e9d747f-cfae-4745-ba16-03b0df84377a', NULL, 'What is the SI unit of force?', 'Joule', 'Pascal', 'Newton', 'Watt', 'C', 'The SI unit of force is the Newton (N), named after Sir Isaac Newton in recognition of his work on classical mechanics.', 'easy', '2025-09-09 06:29:35.031');
INSERT INTO public.questions (id, topic_id, question_text, option_a, option_b, option_c, option_d, correct_answer, explanation, difficulty, created_at) VALUES ('34914c9d-599f-41f2-b3d4-5e2320f3bf60', NULL, 'Which of the following instruments is used to measure atmospheric pressure?', 'Thermometer', 'Barometer', 'Hygrometer', 'Anemometer', 'B', 'A barometer is used to measure atmospheric pressure. It can be used in weather forecasting to predict short-term changes in the weather.', 'easy', '2025-09-09 06:29:35.079');
INSERT INTO public.questions (id, topic_id, question_text, option_a, option_b, option_c, option_d, correct_answer, explanation, difficulty, created_at) VALUES ('2dfd2442-7452-46be-9e08-91944a7f8678', NULL, 'What is the primary purpose of a fuse in an electrical circuit?', 'To increase current flow', 'To decrease voltage', 'To protect against overcurrent', 'To conduct electricity', 'C', 'A fuse is a safety device used to protect an electrical circuit by breaking the connection if the current exceeds a safe level, thereby preventing overcurrent.', 'easy', '2025-09-09 06:29:35.129');
INSERT INTO public.questions (id, topic_id, question_text, option_a, option_b, option_c, option_d, correct_answer, explanation, difficulty, created_at) VALUES ('4f038f36-2a46-4b6b-8921-05b0c89457b9', NULL, 'Which one of the following is a renewable source of energy?', 'Coal', 'Natural gas', 'Solar energy', 'Nuclear energy', 'C', 'Solar energy is renewable because it is derived from the sun, which is a consistent and ongoing source of power.', 'easy', '2025-09-09 06:29:35.178');
INSERT INTO public.questions (id, topic_id, question_text, option_a, option_b, option_c, option_d, correct_answer, explanation, difficulty, created_at) VALUES ('f9f5b373-1038-4bff-9cfa-a7d14a6a87e3', NULL, 'What is the effect of increasing the temperature of a gas at constant volume?', 'The gas pressure decreases', 'The gas pressure remains constant', 'The gas pressure increases', 'The gas expands', 'C', 'According to Gay-Lussac''s law, the pressure of a gas increases with an increase in temperature if the volume remains constant.', 'medium', '2025-09-09 06:29:35.224');
INSERT INTO public.questions (id, topic_id, question_text, option_a, option_b, option_c, option_d, correct_answer, explanation, difficulty, created_at) VALUES ('7211fc0e-a54f-409b-b606-ff47f9199683', NULL, 'Which material is a good conductor of electricity?', 'Rubber', 'Glass', 'Copper', 'Wood', 'C', 'Copper is a good conductor of electricity due to its high electrical conductivity, making it commonly used in electrical wiring.', 'easy', '2025-09-09 06:29:35.272');
INSERT INTO public.questions (id, topic_id, question_text, option_a, option_b, option_c, option_d, correct_answer, explanation, difficulty, created_at) VALUES ('804fe0c3-a312-40b2-ba6a-bd3b7b7f24a6', NULL, 'In which of the following media does sound travel the fastest?', 'Air', 'Water', 'Iron', 'Vacuum', 'C', 'Sound travels fastest in solids like iron because the particles are closely packed, allowing sound waves to transmit more efficiently.', 'medium', '2025-09-09 06:29:35.32');
INSERT INTO public.questions (id, topic_id, question_text, option_a, option_b, option_c, option_d, correct_answer, explanation, difficulty, created_at) VALUES ('2926bdf3-e207-4130-bd34-50bb9371c120', NULL, 'Which law describes the relationship between the pressure and volume of a gas at constant temperature?', 'Boyle''s Law', 'Charles''s Law', 'Pascal''s Law', 'Newton''s Law', 'A', 'Boyle''s Law states that the pressure of a gas is inversely proportional to its volume at constant temperature.', 'medium', '2025-09-09 06:29:35.367');
INSERT INTO public.questions (id, topic_id, question_text, option_a, option_b, option_c, option_d, correct_answer, explanation, difficulty, created_at) VALUES ('435d4fb7-7cc7-43c7-99e4-1627ac26c669', NULL, 'What is the acceleration due to gravity on Earth?', '9.8 m/s', '9.8 m/sÂ²', '10.8 m/sÂ²', '9.8 km/s', 'B', 'The acceleration due to gravity on Earth is approximately 9.8 m/sÂ², which is the rate at which objects accelerate towards the Earth when in free fall.', 'easy', '2025-09-09 06:29:35.415');
INSERT INTO public.questions (id, topic_id, question_text, option_a, option_b, option_c, option_d, correct_answer, explanation, difficulty, created_at) VALUES ('7df6c608-e485-46eb-b55d-94fd17f0e7f1', NULL, 'What is the term for the bending of light as it passes from one medium to another?', 'Reflection', 'Diffraction', 'Refraction', 'Dispersion', 'C', 'Refraction is the bending of light as it passes from one medium to another due to a change in its speed.', 'medium', '2025-09-09 06:29:35.463');
INSERT INTO public.questions (id, topic_id, question_text, option_a, option_b, option_c, option_d, correct_answer, explanation, difficulty, created_at) VALUES ('e1c3d908-c6b1-46fd-adbf-c53fd0e97df5', NULL, 'Which of the following is a characteristic of a wave?', 'Mass', 'Volume', 'Amplitude', 'Density', 'C', 'Amplitude is a characteristic of a wave that measures the maximum displacement of points on the wave from its rest position.', 'medium', '2025-09-09 06:29:35.51');
INSERT INTO public.questions (id, topic_id, question_text, option_a, option_b, option_c, option_d, correct_answer, explanation, difficulty, created_at) VALUES ('5e2fbb4f-bdc3-4ec0-b6bc-f03300da92d7', NULL, 'What type of image is formed by a plane mirror?', 'Real and inverted', 'Virtual and erect', 'Real and magnified', 'Virtual and inverted', 'B', 'A plane mirror forms a virtual and erect image that is the same size as the object.', 'easy', '2025-09-09 06:29:35.558');
INSERT INTO public.questions (id, topic_id, question_text, option_a, option_b, option_c, option_d, correct_answer, explanation, difficulty, created_at) VALUES ('fec2c389-4e1b-4f5d-9442-7e3e78b4578c', NULL, 'What is the principle behind the operation of a hydraulic press?', 'Archimedes'' Principle', 'Pascal''s Principle', 'Bernoulli''s Principle', 'Newton''s Third Law', 'B', 'Pascal''s Principle states that a change in pressure applied to a confined fluid is transmitted undiminished throughout the fluid, which is the principle behind hydraulic systems.', 'medium', '2025-09-09 06:29:35.606');
INSERT INTO public.questions (id, topic_id, question_text, option_a, option_b, option_c, option_d, correct_answer, explanation, difficulty, created_at) VALUES ('2e9a4b8a-2589-45ee-983d-a49e636404a4', NULL, 'What is the unit of electric current?', 'Volt', 'Ampere', 'Ohm', 'Watt', 'B', 'The unit of electric current is the Ampere (A), which measures the flow of electric charge.', 'easy', '2025-09-09 06:29:35.655');
INSERT INTO public.questions (id, topic_id, question_text, option_a, option_b, option_c, option_d, correct_answer, explanation, difficulty, created_at) VALUES ('61f77264-b66f-48a9-9312-fc6d74a1b385', NULL, 'Which of the following substances would float on water?', 'Iron', 'Aluminium', 'Ice', 'Copper', 'C', 'Ice floats on water because it is less dense than liquid water, due to the molecular structure of ice.', 'easy', '2025-09-09 06:29:35.703');
INSERT INTO public.questions (id, topic_id, question_text, option_a, option_b, option_c, option_d, correct_answer, explanation, difficulty, created_at) VALUES ('4333ad89-add8-43ac-be16-ff1e43e1217d', NULL, 'Which of the following devices converts chemical energy into electrical energy?', 'Transformer', 'Battery', 'Generator', 'Motor', 'B', 'A battery converts chemical energy into electrical energy through electrochemical reactions within its cells.', 'medium', '2025-09-09 06:29:35.75');
INSERT INTO public.questions (id, topic_id, question_text, option_a, option_b, option_c, option_d, correct_answer, explanation, difficulty, created_at) VALUES ('44014381-64b9-4fd7-9e51-32af5ff550e8', NULL, 'What is the speed of light in a vacuum?', '3 x 10^6 m/s', '3 x 10^8 m/s', '3 x 10^9 m/s', '3 x 10^7 m/s', 'B', 'The speed of light in a vacuum is approximately 3 x 10^8 meters per second, which is a fundamental constant of nature.', 'medium', '2025-09-09 06:29:35.798');
INSERT INTO public.questions (id, topic_id, question_text, option_a, option_b, option_c, option_d, correct_answer, explanation, difficulty, created_at) VALUES ('947cbbfa-aebe-4336-9f73-4f5ba8b5dd1d', NULL, 'What is the main factor that affects the pitch of a sound?', 'Amplitude', 'Frequency', 'Wavelength', 'Velocity', 'B', 'The pitch of a sound is determined by its frequency; higher frequencies correspond to higher pitches.', 'medium', '2025-09-09 06:29:35.845');
INSERT INTO public.questions (id, topic_id, question_text, option_a, option_b, option_c, option_d, correct_answer, explanation, difficulty, created_at) VALUES ('860ea11b-1098-4f73-9f75-b3b4e0c1c74c', NULL, 'Which of the following is not a thermometric property?', 'Volume of a gas', 'Resistance of a wire', 'Pressure of a gas', 'Mass of a liquid', 'D', 'Mass of a liquid is not a thermometric property; thermometric properties change with temperature and can be used to measure it, like volume, resistance, and pressure.', 'medium', '2025-09-09 06:29:35.893');
INSERT INTO public.questions (id, topic_id, question_text, option_a, option_b, option_c, option_d, correct_answer, explanation, difficulty, created_at) VALUES ('4008b23f-329a-4ae5-9837-89fbfcf495c7', NULL, 'Which type of lens is used to correct short-sightedness (myopia)?', 'Convex lens', 'Concave lens', 'Bifocal lens', 'Cylindrical lens', 'B', 'A concave lens is used to correct short-sightedness by diverging light rays before they reach the eye, enabling a proper focus on the retina.', 'medium', '2025-09-09 06:29:35.939');
INSERT INTO public.questions (id, topic_id, question_text, option_a, option_b, option_c, option_d, correct_answer, explanation, difficulty, created_at) VALUES ('ed21c922-da22-491b-82ae-3604dbc942e6', NULL, 'Which of the following is a property of all electromagnetic waves?', 'They require a medium to travel', 'They travel at the same speed in a vacuum', 'They are longitudinal waves', 'They have the same frequency', 'B', 'All electromagnetic waves travel at the same speed in a vacuum, which is the speed of light, approximately 3 x 10^8 m/s.', 'medium', '2025-09-09 06:29:35.986');
INSERT INTO public.questions (id, topic_id, question_text, option_a, option_b, option_c, option_d, correct_answer, explanation, difficulty, created_at) VALUES ('3457d795-eb85-4a1c-b14e-f68e60f732d2', NULL, 'What happens to the current in a series circuit if one of the components fails?', 'The current increases', 'The current decreases', 'The current stops', 'The current remains the same', 'C', 'In a series circuit, if one component fails, the entire circuit is broken, and the current stops flowing.', 'easy', '2025-09-09 06:29:36.034');
INSERT INTO public.questions (id, topic_id, question_text, option_a, option_b, option_c, option_d, correct_answer, explanation, difficulty, created_at) VALUES ('2236a154-9849-4b38-933a-f228e3326b12', NULL, 'Which of the following is not an effect of electric current?', 'Magnetic effect', 'Chemical effect', 'Thermal effect', 'Optical effect', 'D', 'Electric current can produce magnetic, chemical, and thermal effects, but not optical effects directly.', 'medium', '2025-09-09 06:29:36.081');
INSERT INTO public.questions (id, topic_id, question_text, option_a, option_b, option_c, option_d, correct_answer, explanation, difficulty, created_at) VALUES ('7e8a7e13-592d-4928-bae9-57455426d3fe', NULL, 'Which of the following phenomena demonstrates the wave nature of light?', 'Photoelectric effect', 'Interference', 'Emission spectra', 'Reflection', 'B', 'Interference is a phenomenon that demonstrates the wave nature of light, as it involves the superposition of light waves leading to regions of constructive and destructive interference.', 'medium', '2025-09-09 06:29:36.129');
INSERT INTO public.questions (id, topic_id, question_text, option_a, option_b, option_c, option_d, correct_answer, explanation, difficulty, created_at) VALUES ('0c2baa47-4f15-4056-b824-d4dd221c33d9', NULL, 'What is the name of the force that opposes the relative motion of two surfaces in contact?', 'Gravitational force', 'Normal force', 'Frictional force', 'Tension force', 'C', 'Frictional force is the force that opposes the relative motion or tendency of such motion of two surfaces in contact.', 'easy', '2025-09-09 06:29:36.177');
INSERT INTO public.questions (id, topic_id, question_text, option_a, option_b, option_c, option_d, correct_answer, explanation, difficulty, created_at) VALUES ('110ad20a-0391-4b96-96ae-e5ffe23327cc', NULL, 'Which component is used to store electrical energy in a circuit?', 'Resistor', 'Capacitor', 'Inductor', 'Diode', 'B', 'A capacitor is an electrical component used to store energy electrostatically in an electric field.', 'medium', '2025-09-09 06:29:36.225');
INSERT INTO public.questions (id, topic_id, question_text, option_a, option_b, option_c, option_d, correct_answer, explanation, difficulty, created_at) VALUES ('9830b148-3a59-4ccd-ac3b-2a9748fd5854', NULL, 'What is the function of a transformer in an electrical circuit?', 'To generate electricity', 'To convert AC to DC', 'To change the voltage', 'To store energy', 'C', 'A transformer is used in electrical circuits to change the voltage level, either increasing (step-up) or decreasing (step-down) the voltage.', 'medium', '2025-09-09 06:29:36.272');
INSERT INTO public.questions (id, topic_id, question_text, option_a, option_b, option_c, option_d, correct_answer, explanation, difficulty, created_at) VALUES ('25508c5c-e2bb-4169-89aa-d3f480f7b6ca', NULL, 'Which of the following is the best conductor of electricity?', 'Silver', 'Gold', 'Copper', 'Aluminum', 'A', 'Silver is the best conductor of electricity due to its highest electrical conductivity among all metals, though copper is more commonly used due to cost considerations.', 'medium', '2025-09-09 06:29:36.32');
INSERT INTO public.questions (id, topic_id, question_text, option_a, option_b, option_c, option_d, correct_answer, explanation, difficulty, created_at) VALUES ('570530d4-fcf7-42fd-8ece-7a260334a2b4', NULL, 'What is the term for the resistance to motion when an object is moving through a fluid?', 'Friction', 'Drag', 'Tension', 'Upthrust', 'B', 'Drag is the resistance force caused by the motion of an object through a fluid, such as air or water.', 'medium', '2025-09-09 06:29:36.372');


